mapper.select:
SELECT * FROM recipe_favorites 
WHERE user_id = #{userId}
ORDER BY created_at DESC
LIMIT #{limit} OFFSET #{offset}

SELECT r.*, rf.created_at AS favorite_time, rf.plan_cook_date
FROM recipe_favorites rf
JOIN recipes r ON r.id = rf.recipe_id
WHERE rf.user_id = #{userId}
ORDER BY rf.created_at DESC
LIMIT #{limit} OFFSET #{offset}

SELECT * FROM recipe_favorites 
WHERE recipe_id = #{recipeId}
ORDER BY created_at DESC
LIMIT #{limit} OFFSET #{offset}

SELECT * FROM recipe_favorites
WHERE user_id = #{userId}
  AND plan_cook_date IS NOT NULL

  AND plan_cook_date = #{targetDate}

ORDER BY plan_cook_date ASC
