mapper.select:
SELECT * FROM recipes

  ORDER BY id DESC LIMIT #{limit}


  ORDER BY id DESC

SELECT * FROM recipes
WHERE type_id = #{categoryId}

  ORDER BY id DESC LIMIT #{limit}


  ORDER BY id DESC

SELECT r.* 
FROM recipes r
JOIN recipe_category_relation rcr ON r.id = rcr.recipe_id
WHERE rcr.category_id = #{categoryId}

  ORDER BY r.id DESC LIMIT #{limit}


  ORDER BY r.id DESC

SELECT * FROM recipes WHERE 1=1
AND (name LIKE CONCAT('%', #{keyword}, '%') OR feature LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY id DESC LIMIT #{limit} OFFSET #{offset}

SELECT r.*, /* 如果存在一对一分类则直接用之，否则合并多分类名称 */ COALESCE(c.category_name, GROUP_CONCAT(DISTINCT c2.category_name ORDER BY c2.category_name SEPARATOR '、')) AS category_name, /* 供前端编辑回填用：以逗号字符串返回 category_id 集合 */ GROUP_CONCAT(DISTINCT rcr.category_id ORDER BY rcr.category_id) AS category_ids FROM recipes r LEFT JOIN recipe_categories c ON c.category_id = r.id LEFT JOIN recipe_category_relation rcr ON rcr.recipe_id = r.id LEFT JOIN recipe_categories c2 ON c2.category_id = rcr.category_id WHERE 1=1
AND (r.name LIKE CONCAT('%', #{keyword}, '%') OR r.feature LIKE CONCAT('%', #{keyword}, '%'))
GROUP BY r.id ORDER BY r.id DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE recipes
type_id = #{typeId}, name = #{name}, method = #{method}, condiments = #{condiments}, ingredients = #{ingredients}, feature = #{feature},
WHERE id = #{id}

mapper.select:
SELECT * FROM recipes
ORDER BY RAND()
LIMIT #{limit}
